import React, { useState } from 'react';
import { Switch } from '@/components/ui/switch';

const examData = [
  { id: "0001", lembaga: "SMKN 1 SURABAYA", jumlahPeserta: 100, tanggal: "01/03/24" },
  { id: "0002", lembaga: "SMKN 2 SURABAYA", jumlahPeserta: 115, tanggal: "01/03/24" },
  { id: "0003", lembaga: "SMKN 3 SURABAYA", jumlahPeserta: 102, tanggal: "01/03/24" },
  { id: "0004", lembaga: "SMKN 4 SURABAYA", jumlahPeserta: 105, tanggal: "01/03/24" },
  { id: "0005", lembaga: "SMKN 5 SURABAYA", jumlahPeserta: 97, tanggal: "01/03/24" },
  { id: "0006", lembaga: "SMKN 6 SURABAYA", jumlahPeserta: 110, tanggal: "10/05/24" },
  { id: "0007", lembaga: "SMKN 7 SURABAYA", jumlahPeserta: 100, tanggal: "10/05/24" },
  { id: "0008", lembaga: "SMKN 8 SURABAYA", jumlahPeserta: 108, tanggal: "10/05/24" },
  { id: "0009", lembaga: "SMKN 9 SURABAYA", jumlahPeserta: 119, tanggal: "10/05/24" },
  { id: "0010", lembaga: "SMKN 10 SURABAYA", jumlahPeserta: 102, tanggal: "10/05/24" },
];

const Ujian = () => {
  const [statuses, setStatuses] = useState(examData.reduce((acc, exam) => ({...acc, [exam.id]: false}), {}));

  const handleStatusChange = (id) => {
    setStatuses(prev => ({...prev, [id]: !prev[id]}));
  };

  return (
    <div className="overflow-x-auto">
      <table className="min-w-full bg-white border border-gray-200 rounded-lg overflow-hidden">
        <thead className="bg-gray-50">
          <tr>
            <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
            <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">ID Ujian</th>
            <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Lembaga</th>
            <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Jumlah Peserta</th>
            <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Tanggal</th>
          </tr>
        </thead>
        <tbody className="bg-white divide-y divide-gray-200">
          {examData.map((exam) => (
            <tr key={exam.id}>
              <td className="px-4 py-2 whitespace-nowrap">
                <Switch
                  checked={statuses[exam.id]}
                  onCheckedChange={() => handleStatusChange(exam.id)}
                />
              </td>
              <td className="px-4 py-2 whitespace-nowrap text-sm font-medium text-gray-900">{exam.id}</td>
              <td className="px-4 py-2 whitespace-nowrap text-sm text-gray-500">{exam.lembaga}</td>
              <td className="px-4 py-2 whitespace-nowrap text-sm text-gray-500">{exam.jumlahPeserta}</td>
              <td className="px-4 py-2 whitespace-nowrap text-sm text-gray-500">{exam.tanggal}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Ujian;
