import React, { useState, useEffect } from 'react';
import { LayoutGrid } from 'lucide-react';

const QuestionComponent = () => {
  const [timeLeft, setTimeLeft] = useState(3540); // 59 minutes in seconds

  useEffect(() => {
    const timer = setInterval(() => {
      setTimeLeft((prevTime) => (prevTime > 0 ? prevTime - 1 : 0));
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const seconds = time % 60;
    return `${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col">
      <div className="flex-grow bg-white shadow-md p-4 sm:p-6 md:p-8 lg:p-10">
        <div className="flex justify-between items-center mb-6 md:mb-8">
          <h2 className="text-2xl sm:text-3xl md:text-4xl font-bold">Soal 2</h2>
          <div className="flex items-center">
            <span className="mr-4 font-mono text-xl sm:text-2xl md:text-3xl">{formatTime(timeLeft)}</span>
            <LayoutGrid className="w-6 h-6 sm:w-8 sm:h-8 md:w-10 md:h-10" />
          </div>
        </div>

        <p className="text-lg sm:text-xl md:text-2xl mb-6 md:mb-8">Perhatikan gambar dibawah ini!</p>

        <div className="w-40 h-40 sm:w-48 sm:h-48 md:w-56 md:h-56 lg:w-64 lg:h-64 border-4 border-black mb-8 md:mb-10 p-3 mx-auto">
          <div className="relative w-full h-full">
            <div className="absolute inset-0 border-4 border-black rounded-full"></div>
            <div className="absolute top-0 left-0 w-1/2 h-1/2 border-r-4 border-b-4 border-black"></div>
            <div className="absolute top-0 left-0 w-1/4 h-1/4 bg-black rounded-tl-full"></div>
          </div>
        </div>

        <p className="text-lg sm:text-xl md:text-2xl mb-6 md:mb-8">Pilih jawaban yang benar....</p>

        <div className="grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-5 gap-4 sm:gap-6 md:gap-8 mb-8 md:mb-10">
          {['A', 'B', 'C', 'D', 'E'].map((option) => (
            <div key={option} className="flex items-center">
              <input type="radio" id={option} name="answer" className="mr-3 w-5 h-5" />
              <label htmlFor={option} className="flex items-center text-lg sm:text-xl">
                <span className="mr-3 font-bold">{option}.</span>
                <div className="w-20 h-20 sm:w-24 sm:h-24 md:w-28 md:h-28 border-2 border-black p-1">
                  <div className="w-full h-full bg-gray-200"></div>
                </div>
              </label>
            </div>
          ))}
        </div>

        <div className="flex justify-between">
          <button className="px-6 py-3 bg-blue-500 text-white rounded-md text-lg sm:text-xl">
            Kembali
          </button>
          <button className="px-6 py-3 bg-blue-500 text-white rounded-md text-lg sm:text-xl">
            Lanjut
          </button>
        </div>
      </div>
    </div>
  );
};

export default QuestionComponent;
