import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Soal = () => {
    const [hasCompletedExam1, setHasCompletedExam1] = useState(false);
    const [isConfirmationVisible, setIsConfirmationVisible] = useState(false);
    const [isNotificationVisible, setIsNotificationVisible] = useState(false);
    const [notificationMessage, setNotificationMessage] = useState('');
    const [currentExam, setCurrentExam] = useState('');

    const toggleSidebar = () => {
        const sidebar = document.getElementById("sidebar");
        sidebar.classList.toggle("w-14");
        sidebar.classList.toggle("w-56");

        const navTexts = sidebar.querySelectorAll('.nav-text');
        navTexts.forEach(text => {
            text.classList.toggle('hidden');
        });
    };

    const checkExamCompletion = (ujianName) => {
        if (!hasCompletedExam1) {
            showNotification("Anda belum mengerjakan Ujian 1");
        } else {
            showConfirmation(ujianName);
        }
    };

    const showConfirmation = (ujianName) => {
        setCurrentExam(ujianName);
        setIsConfirmationVisible(true);
    };

    const showNotification = (message) => {
        setNotificationMessage(message);
        setIsNotificationVisible(true);
    };

    const closePopup = () => {
        setIsConfirmationVisible(false);
    };

    const closeNotification = () => {
        setIsNotificationVisible(false);
    };

    const handleStartExam = () => {
        alert(`Anda memulai ${currentExam}`);
        closePopup();
        // Tambahkan logika untuk memulai ujian di sini
    };

    // Simulasi pengerjaan ujian 1
    const handleExam1Completion = () => {
        setHasCompletedExam1(true);
    };

    return (
        <div className="flex min-h-screen bg-gray-100">
            <div className="sidebar bg-blue-500 flex flex-col items-center justify-between py-5 px-0 fixed left-10 top-1/2 transform -translate-y-1/2 h-[95%] w-14 rounded-lg transition-all duration-300" id="sidebar">
                <button className="toggle-btn mb-5" onClick={toggleSidebar}>
                    <i className="fas fa-bars text-white text-2xl"></i>
                </button>
                <Link to="#" className="nav-item flex items-center justify-start w-full py-2 px-4 mb-5 text-white transition-colors duration-300">
                    <i className="fas fa-file-alt text-2xl mr-2"></i>
                    <span className="nav-text hidden text-base" onClick={() => checkExamCompletion("Ujian 1")}>Soal Ujian</span>
                </Link>
                <Link to="/registration" className="nav-item logout mt-auto flex items-center justify-start w-full py-2 px-4 text-white transition-colors duration-300">
                    <i className="fas fa-sign-out-alt text-2xl mr-2"></i>
                    <span className="nav-text hidden text-base">Logout</span>
                </Link>
            </div>

            <div className="container flex-1 ml-20 p-10">
                <h2 className="text-3xl mb-6">Soal Ujian</h2>

                <div className="absolute top-5 right-12 mt-6 ml-5">
                    <i className="fas fa-user text-2xl"></i>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                    <div className="card bg-white p-6 rounded-lg shadow-md">
                        <h3 className="text-xl font-bold mb-2">Ujian 1</h3>
                        <p>Waktu: 60 Menit<br />Jumlah Soal: 50</p>
                        <button className="mt-4 bg-black text-white py-2 px-4 rounded hover:bg-gray-800" onClick={() => { handleExam1Completion(); checkExamCompletion("Ujian 1"); }}>Mulai</button>
                    </div>
                    <div className="card bg-white p-6 rounded-lg shadow-md">
                        <h3 className="text-xl font-bold mb-2">Ujian 2</h3>
                        <p>Waktu: 60 Menit<br />Jumlah Soal: 50</p>
                        <button className="mt-4 bg-black text-white py-2 px-4 rounded hover:bg-gray-800" onClick={() => checkExamCompletion("Ujian 2")}>Mulai</button>
                    </div>
                    <div className="card bg-white p-6 rounded-lg shadow-md">
                        <h3 className="text-xl font-bold mb-2">Ujian 3</h3>
                        <p>Waktu: 60 Menit<br />Jumlah Soal: 50</p>
                        <button className="mt-4 bg-black text-white py-2 px-4 rounded hover:bg-gray-800" onClick={() => checkExamCompletion("Ujian 3")}>Mulai</button>
                    </div>
                    <div className="card bg-white p-6 rounded-lg shadow-md">
                        <h3 className="text-xl font-bold mb-2">Ujian 4</h3>
                        <p>Waktu: 60 Menit<br />Jumlah Soal: 50</p>
                        <button className="mt-4 bg-black text-white py-2 px-4 rounded hover:bg-gray-800" onClick={() => checkExamCompletion("Ujian 4")}>Mulai</button>
                    </div>
                </div>
            </div>

            {/* Confirmation Popup */}
            {isConfirmationVisible && (
                <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
                    <div className="bg-white rounded-lg p-5 text-center">
                        <h3 className="text-lg font-bold mb-3">Apakah Anda ingin memulai ujian?</h3>
                        <div>
                            <button className="bg-green-500 text-white px-4 py-2 rounded mr-2" onClick={handleStartExam}>Ya</button>
                            <button className="bg-red-500 text-white px-4 py-2 rounded" onClick={closePopup}>Tidak</button>
                        </div>
                    </div>
                </div>
            )}

            {/* Notification Popup */}
            {isNotificationVisible && (
                <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
                    <div className="bg-white rounded-lg p-5 text-center">
                        <h3 className="text-lg font-bold mb-3">{notificationMessage}</h3>
                        <button className="bg-blue-500 text-white px-4 py-2 rounded" onClick={closeNotification}>OK</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Soal;
