import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '@fortawesome/fontawesome-free/css/all.min.css';
import { Menu, LogOut, FileText, ChevronLeft } from 'lucide-react';

export function Soal() {
    const [isOpen, setIsOpen] = useState(false);

    const toggleSidebar = () => {
        setIsOpen(!isOpen);
    };

    return (
        <div className="h-screen bg-gray-200 text-gray-900 flex">
            <nav className={`flex flex-col transition-all duration-300 ease-in-out ${isOpen ? 'w-64' : 'w-16'} bg-blue-400`}>
                <button
                    className="w-full py-3 px-4 flex items-center justify-between hover:bg-blue-300 transition-colors duration-200"
                    onClick={toggleSidebar}>
                    <div className="flex items-center">
                        <Menu className="w-6 h-6 mr-3 text-white" />
                        {isOpen && <span className="text-lg font-medium text-white">Menu</span>}
                    </div>
                    {isOpen && <ChevronLeft className="w-5 h-5 text-white" />}
                </button>

                <Link to="/soal" className="w-full py-3 px-4 flex items-center hover:bg-blue-300 transition-colors duration-200">
                    <FileText className="w-6 h-6 mr-3 text-white" />
                    {isOpen && <span className="text-white">Soal</span>}
                </Link>

                <div className="mt-auto">
                    <Link to="/registration" className="w-full py-3 px-4 flex items-center hover:bg-blue-300 transition-colors duration-200">
                        <LogOut className="w-6 h-6 mr-3 text-white" />
                        {isOpen && <span className="text-white">Logout</span>}
                    </Link>
                </div>
            </nav>

            <div className="bg-white flex-grow p-10">
                <h2 className="text-3xl mb-6">Soal Ujian</h2>
                <div className="absolute top-7 right-12 mt-6">
                    <i className="fas fa-user shadow-lg text-2xl"></i>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                    {[1, 2, 3, 4].map((ujian) => (
                        <div
                            className="card bg-white p-6 rounded-xl shadow-lg flex flex-col justify-between h-[200px] w-[275px] border border-gray-300"
                            key={ujian}>
                            <h3 className="text-lg font-bold mb-1">Ujian {ujian}</h3>
                            <p>Waktu: 60 Menit<br />Jumlah Soal: 50</p>
                            <button
                                className="mt-4 bg-black text-white py-2 px-6 rounded-full text-sm hover:bg-gray-800">
                                Mulai
                            </button>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default Soal;
