import React, { useState } from 'react';
import { LayoutGrid } from 'lucide-react';

const QuestionComponent = () => {
  const [showGrid, setShowGrid] = useState(false);
  const [selectedNumber, setSelectedNumber] = useState(1);

  const toggleGrid = () => {
    setShowGrid(!showGrid);
  };

  const handleNumberSelection = (number) => {
    setSelectedNumber(number);
    setShowGrid(false);
  };

  const NumberGrid = () => (
    <div className="absolute top-full right-0 mt-2 bg-white border border-gray-300 rounded-lg shadow-xl p-4 z-10 w-64 transition-all duration-300 ease-in-out">
      <div className="grid grid-cols-4 gap-3">
        {[...Array(20)].map((_, index) => (
          <button
            key={index}
            className="w-12 h-12 flex items-center justify-center border border-gray-300 rounded-lg text-lg font-semibold hover:bg-blue-100 transition-colors duration-200"
            onClick={() => handleNumberSelection(index + 1)}
          >
            {index + 1}
          </button>
        ))}
      </div>
    </div>
  );

  return (
    <div className="max-w-7xl mx-auto mt-7 p-6 bg-white rounded-lg shadow-2xl">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-2xl font-bold">Soal {selectedNumber}</h2>
        <div className="flex items-center relative">
          <button
            className="flex items-center space-x-2 bg-gray-100 hover:bg-gray-200 py-2 px-4 rounded-lg transition-colors duration-200"
            onClick={toggleGrid}
          >
            <span className="font-mono text-lg">{selectedNumber.toString().padStart(2, '0')}</span>
            <LayoutGrid className="w-5 h-5" />
          </button>
          {showGrid && <NumberGrid />}
        </div>
      </div>

      <div className="mt-10 space-y-6">
        <p className="text-lg">Perhatikan gambar dibawah ini!</p>
        <img src="/assets/no1.png" alt="Gambar Soal" className="w-full max-w-md mx-auto"/>

        <p className="text-lg">Pilih jawaban yang benar....</p>

        <div className="grid grid-cols-3 gap-6">
          {['A', 'B', 'C', 'D', 'E'].map((option) => (
            <div key={option} className="flex items-center space-x-3">
              <input type="radio" id={option} name="answer" className="w-4 h-4" />
              <label htmlFor={option} className="flex items-center">
                <span className="mr-2 font-semibold">{option}.</span>
                <div className="w-32 h-32 border-2 border-gray-300 rounded-lg p-1 hover:border-blue-400 transition-colors duration-200">
                  <img src={`/assets/1${option.toLowerCase()}.png`} alt={`Opsi ${option}`} className="w-full h-full object-cover"/>
                </div>
              </label>
            </div>
          ))}
        </div>
      </div>

      <div className="flex justify-between mt-12">
        <button className="px-6 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors duration-200">Kembali</button>
        <button className="px-6 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors duration-200">Lanjut</button>
      </div>
    </div>
  );
};

export default QuestionComponent;
