import React, { useState, useEffect } from 'react';
import { LayoutGrid } from 'lucide-react';

const QuestionComponent = () => {
  const [timeLeft, setTimeLeft] = useState(3540); // 59 minutes in seconds

  useEffect(() => {
    const timer = setInterval(() => {
      setTimeLeft((prevTime) => (prevTime > 0 ? prevTime - 1 : 0));
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const seconds = time % 60;
    return `${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4">
      <div className="w-full max-w-4xl bg-white rounded-lg shadow-md overflow-hidden">
        <div className="p-6 md:p-8 lg:p-10">
          <div className="flex justify-between items-center mb-8">
            <h2 className="text-2xl md:text-3xl font-bold">Soal 2</h2>
            <div className="flex items-center">
              <span className="mr-4 font-mono text-xl md:text-2xl">{formatTime(timeLeft)}</span>
              <LayoutGrid className="w-8 h-8" />
            </div>
          </div>

          <p className="text-lg md:text-xl mb-6">Perhatikan gambar dibawah ini!</p>

          <div className="w-40 h-40 md:w-48 md:h-48 lg:w-56 lg:h-56 border-4 border-black mb-8 p-3 mx-auto">
            <div className="relative w-full h-full">
              <div className="absolute inset-0 border-4 border-black rounded-full"></div>
              <div className="absolute top-0 left-0 w-1/2 h-1/2 border-r-4 border-b-4 border-black"></div>
              <div className="absolute top-0 left-0 w-1/4 h-1/4 bg-black rounded-tl-full"></div>
            </div>
          </div>

          <p className="text-lg md:text-xl mb-6">Pilih jawaban yang benar....</p>

          <div className="grid grid-cols-2 md:grid-cols-3 gap-6 mb-8">
            {['A', 'B', 'C', 'D', 'E'].map((option) => (
              <div key={option} className="flex items-center">
                <input type="radio" id={option} name="answer" className="mr-3 w-5 h-5" />
                <label htmlFor={option} className="flex items-center text-lg">
                  <span className="mr-3 font-bold">{option}.</span>
                  <div className="w-20 h-20 md:w-24 md:h-24 border-2 border-black p-1">
                    <div className="w-full h-full bg-gray-200"></div>
                  </div>
                </label>
              </div>
            ))}
          </div>

          <div className="flex justify-between">
            <button className="px-6 py-3 bg-blue-500 text-white rounded-md text-lg">
              Kembali
            </button>
            <button className="px-6 py-3 bg-blue-500 text-white rounded-md text-lg">
              Lanjut
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default QuestionComponent;
