import React from 'react';
import { Link, useLocation } from 'react-router-dom';

function Soal() {
    const location = useLocation();
    const currentPath = location.pathname; // Mendapatkan path saat ini

    const handleButtonClick = (ujianName) => {
        // Logika tambahan bisa ditambahkan di sini
        console.log(`Memulai ${ujianName}`);
    };

    const handleLinkClick = (ujianName) => {
        if (currentPath === "/soal") {
            // Jika sudah di halaman soal, lakukan sesuatu
            console.log("Sudah di halaman Soal");
        } else {
            handleButtonClick(ujianName);
        }
    };

    return (
        <div className="container flex-1 ml-20 p-10">
            <h2 className="text-3xl mb-6">Soal Ujian</h2>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                <div className="card bg-white p-6 rounded-lg shadow-md">
                    <h3 className="text-xl font-bold mb-2">Ujian 1</h3>
                    <p>Waktu: 60 Menit<br />Jumlah Soal: 50</p>
                    <Link 
                        to="/soal" 
                        className="mt-4 bg-black text-white py-2 px-4 rounded hover:bg-gray-800" 
                        onClick={() => handleLinkClick("Ujian 1")}
                    >
                        Mulai
                    </Link>
                </div>
                <div className="card bg-white p-6 rounded-lg shadow-md">
                    <h3 className="text-xl font-bold mb-2">Ujian 2</h3>
                    <p>Waktu: 60 Menit<br />Jumlah Soal: 50</p>
                    <Link 
                        to="/soal" 
                        className="mt-4 bg-black text-white py-2 px-4 rounded hover:bg-gray-800" 
                        onClick={() => handleLinkClick("Ujian 2")}
                    >
                        Mulai
                    </Link>
                </div>
                <div className="card bg-white p-6 rounded-lg shadow-md">
                    <h3 className="text-xl font-bold mb-2">Ujian 3</h3>
                    <p>Waktu: 60 Menit<br />Jumlah Soal: 50</p>
                    <Link 
                        to="/soal" 
                        className="mt-4 bg-black text-white py-2 px-4 rounded hover:bg-gray-800" 
                        onClick={() => handleLinkClick("Ujian 3")}
                    >
                        Mulai
                    </Link>
                </div>
                <div className="card bg-white p-6 rounded-lg shadow-md">
                    <h3 className="text-xl font-bold mb-2">Ujian 4</h3>
                    <p>Waktu: 60 Menit<br />Jumlah Soal: 50</p>
                    <Link 
                        to="/soal" 
                        className="mt-4 bg-black text-white py-2 px-4 rounded hover:bg-gray-800" 
                        onClick={() => handleLinkClick("Ujian 4")}
                    >
                        Mulai
                    </Link>
                </div>
            </div>
        </div>
    );
}

export default Soal;
