import React, { useState, useEffect } from 'react';
import '@fortawesome/fontawesome-free/css/all.min.css';
import { LayoutGrid } from 'lucide-react';

const QuestionComponent = () => {
  const [timeLeft, setTimeLeft] = useState(3540); // 59 minutes in seconds

  useEffect(() => {
    const timer = setInterval(() => {
      setTimeLeft((prevTime) => (prevTime > 0 ? prevTime - 1 : 0));
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const seconds = time % 60;
    return `${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
  };

  return (
    <div className="max-w-7xl mx-auto mt-9 p-6 bg-white rounded-lg shadow-2xl">
        <div className="mb-60">
            <div className="flex justify-between items-center mb-4">
                <h2 className="text-2xl font-bold">Soal 1</h2>
                <div className="flex items-center">
        <span className="mr-4 font-mono text-lg">{formatTime(timeLeft)}</span>
        <LayoutGrid className="w-6 h-6" />
      </div>
    </div>
    </div>

      <p className="mb-4">Perhatikan gambar dibawah ini!</p>

      <div className="w-24 h-24 border-2 border-black mb-4 p-2">
        <div className="relative w-full h-full">
          <div className="absolute inset-0 border-2 border-black rounded-full"></div>
          <div className="absolute top-0 left-0 w-1/2 h-1/2 border-r-2 border-b-2 border-black"></div>
        </div>
      </div>

      <p className="mb-4">Pilih jawaban yang benar....</p>

      <div className="grid grid-cols-3 gap-4 mb-6">
        {['A', 'B', 'C', 'D', 'E'].map((option) => (
          <div key={option} className="flex items-center">
            <input type="radio" id={option} name="answer" className="mr-2" />
            <label htmlFor={option} className="flex items-center">
              <span className="mr-2">{option}.</span>
              <div className="w-16 h-16 border-2 border-black p-1">
                {/* Placeholder for option image */}
                <div className="w-full h-full bg-gray-200"></div>
              </div>
            </label>
          </div>
        ))}
      </div>

      <div className="flex justify-between">
        <button className="px-4 py-2 bg-blue-400 text-white rounded-md">
          Kembali
        </button>
        <button className="px-4 py-2 bg-blue-400 text-white rounded-md">
          Lanjut
        </button>
      </div>
    </div>
  );
};

export default QuestionComponent;
