import React, { useState, useMemo } from 'react';
import { Link } from 'react-router-dom';
import { Menu, LogOut, FileText, ChevronLeft, X } from 'lucide-react';
import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';

const examData = [
  { id: "0001", lembaga: "SMKN 1 SURABAYA", jumlahPeserta: 100, tanggal: "01/03/24" },
  { id: "0002", lembaga: "SMKN 2 SURABAYA", jumlahPeserta: 115, tanggal: "01/03/24" },
  { id: "0003", lembaga: "SMKN 3 SURABAYA", jumlahPeserta: 102, tanggal: "01/03/24" },
  { id: "0004", lembaga: "SMKN 4 SURABAYA", jumlahPeserta: 105, tanggal: "01/03/24" },
  { id: "0005", lembaga: "SMKN 5 SURABAYA", jumlahPeserta: 97, tanggal: "01/03/24" },
  { id: "0006", lembaga: "SMKN 6 SURABAYA", jumlahPeserta: 110, tanggal: "10/05/24" },
  { id: "0007", lembaga: "SMKN 7 SURABAYA", jumlahPeserta: 100, tanggal: "10/05/24" },
  { id: "0008", lembaga: "SMKN 8 SURABAYA", jumlahPeserta: 108, tanggal: "10/05/24" },
  { id: "0009", lembaga: "SMKN 9 SURABAYA", jumlahPeserta: 119, tanggal: "10/05/24" },
  { id: "0010", lembaga: "SMKN 10 SURABAYA", jumlahPeserta: 102, tanggal: "10/05/24" },
];

const Sidebar = ({ isOpen, toggleSidebar }) => (
  <nav className={`flex flex-col transition-all duration-300 ease-in-out ${isOpen ? 'w-64' : 'w-16'} bg-blue-400 rounded-r-2xl`}>
    <button className="w-full py-3 px-4 flex items-center justify-between hover:bg-blue-300 transition-colors duration-200" onClick={toggleSidebar}>
      <div className="flex items-center">
        <Menu className="w-6 h-6 mr-3 text-white" /> {isOpen && <span className="text-lg font-medium text-white">Menu</span>}
      </div>
