import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '@fortawesome/fontawesome-free/css/all.min.css';

const Sidebar = () => {
    const [isOpen, setIsOpen] = useState(false);

    const toggleSidebar = () => {
        setIsOpen(!isOpen);
    };

    return (
        <div className={`sidebar bg-blue-500 flex flex-col items-center justify-between py-5 px-0 fixed left-10 top-1/2 transform -translate-y-1/2 h-[95%] ${isOpen ? 'w-56' : 'w-14'} rounded-lg transition-all duration-300`}>
            <button className="toggle-btn mb-5" onClick={toggleSidebar}>
                <i className="fas fa-bars text-white text-2xl"></i>
            </button>
            <Link to="/soal" className="nav-item flex items-center justify-start w-full py-2 px-4 mb-5 text-white transition-colors duration-300">
                <i className="fas fa-file-alt text-2xl mr-2"></i><span className={`nav-text ${isOpen ? '' : 'hidden'} text-base`}>Soal Ujian</span>
            </Link>
            <Link to="/logout" className="nav-item logout mt-auto flex items-center justify-start w-full py-2 px-4 text-white transition-colors duration-300">
                <i className="fas fa-sign-out-alt text-2xl mr-2"></i><span className={`nav-text ${isOpen ? '' : 'hidden'} text-base`}>Logout</span>
            </Link>
        </div>
    );
};

export default Sidebar;
