import React, { useState, createContext, useContext } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faColumns, faChartBar, faUserCircle, faBoxes, faShoppingCart, 
  faFileInvoice, faCog, faLifeRing, faSignOutAlt, faArrowLeft 
} from '@fortawesome/free-solid-svg-icons';

const SidebarContext = createContext();

export default function Dashboard() {
  return (
    <div className="flex">
      <Sidebar>
        <SidebarItem icon={faColumns} text="Soal" alert active />
        <SidebarItem icon={faChartBar} text="Lembaga" />
        <SidebarItem icon={faUserCircle} text="Users" />
        <SidebarItem icon={faBoxes} text="Inventory" />
        <SidebarItem icon={faShoppingCart} text="Order" />
        <SidebarItem icon={faFileInvoice} text="Bills" />
      </Sidebar>
      <InputDataForm />
    </div>
  );
}

function Sidebar({ children }) {
  const [expanded, setExpanded] = useState(true);

  return (
    <SidebarContext.Provider value={{ expanded }}>
      <aside className={`h-screen ${expanded ? "w-64" : "w-20"} transition-all duration-300`}>
        <nav className="h-full flex flex-col bg-blue-400 shadow-xl ml-6 my-6 rounded-xl">
          <div className="p-4 pb-2 flex justify-between items-center">
            <button
              onClick={() => setExpanded(!expanded)}
              className="p-1.5 rounded-lg hover:bg-blue-300 transition-colors"
            >
              <FontAwesomeIcon icon={faArrowLeft} className={`transition-transform duration-300 ${expanded ? '' : 'transform rotate-180'}`} />
            </button>
          </div>
          <ul className="flex-1 px-3">{children}</ul>
          <div className="border-t border-blue-300 flex p-3">
            <SidebarItem icon={faSignOutAlt} text="Logout" />
          </div>
        </nav>
      </aside>
    </SidebarContext.Provider>
  );
}

function SidebarItem({ icon, text, active, alert }) {
  const { expanded } = useContext(SidebarContext);

  return (
    <li className={`
      relative flex items-center py-2 px-3 my-1
      font-medium rounded-md cursor-pointer
      transition-colors group
      ${active 
        ? "bg-gradient-to-tr from-indigo-200 to-indigo-100 text-blue-800" 
        : "text-white hover:bg-blue-300"
      }
    `}>
      <FontAwesomeIcon icon={icon} fixedWidth />
      <span className={`
        overflow-hidden transition-all ${expanded ? "w-52 ml-3" : "w-0"}
      `}>
        {text}
      </span>
      {!expanded && (
        <div className={`
          absolute left-full rounded-md px-2 py-1 ml-6
          bg-indigo-100 text-blue-800 text-sm
          invisible opacity-20 -translate-x-3 transition-all
          group-hover:visible group-hover:opacity-100 group-hover:translate-x-0
        `}>
          {text}
        </div>
      )}
    </li>
  );
}

function InputDataForm() {
  const [formData, setFormData] = useState({
    nama: '',
    deskripsi: '',
    instansi: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted:', formData);
    // Here you would typically send the data to a server
  };

  return (
    <div className="bg-white flex-grow flex justify-center items-center">
      <div className="bg-white rounded-lg shadow-2xl p-12 w-[70%] max-w-[800px]">
        <h2 className="text-2xl mb-8 font-bold text-left">Input Data Diri</h2>
        <form onSubmit={handleSubmit} className="space-y-6">
          <InputField
            label="Nama Peserta"
            name="nama"
            value={formData.nama}
            onChange={handleChange}
          />
          <InputField
            label="Deskripsi"
            name="deskripsi"
            value={formData.deskripsi}
            onChange={handleChange}
          />
          <InputField
            label="Nama Instansi"
            name="instansi"
            value={formData.instansi}
            onChange={handleChange}
          />
          <button
            type="submit"
            className="submit-btn bg-black text-white py-2 px-8 rounded-full cursor-pointer text-base mt-8 self-end hover:bg-gray-800"
          >
            Selesai
          </button>
        </form>
      </div>
    </div>
  );
}

function InputField({ label, name, value, onChange }) {
  return (
    <div className="input-group flex flex-col mb-5 w-full">
      <label htmlFor={name} className="text-sm mb-2">
        {label}
      </label>
      <input
        type="text"
        id={name}
        name={name}
        value={value}
        onChange={onChange}
        placeholder={label}
        className="border border-gray-300 rounded-full py-2 px-4 text-base shadow-md w-full"
      />
    </div>
  );
}
