import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Menu, LogOut, FileText, ChevronLeft } from 'lucide-react';
import { Dialog } from '@headlessui/react';

const examData = [
  { id: "0001", lembaga: "SMKN 1 SURABAYA", jumlahPeserta: 100, tanggal: "01/03/24" },
  // data ujian lainnya...
];

export function Ujian() {
  const [isOpen, setIsOpen] = useState(true);
  const [modalOpen, setModalOpen] = useState(false);
  const [newExam, setNewExam] = useState({ nama: '', tanggal: '', status: false, subject: '' });

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewExam({ ...newExam, [name]: value });
  };

  const handleStatusChange = (id) => {
    // logika perubahan status...
  };

  const handleSubmit = () => {
    // Tambahkan logika untuk mengirim data ujian baru
    console.log(newExam);
    // Tutup modal setelah menginput data
    setModalOpen(false);
    // Reset input
    setNewExam({ nama: '', tanggal: '', status: false, subject: '' });
  };

  return (
    <div className={`h-screen bg-white text-gray-900 flex`}>
      <nav className={`flex flex-col transition-all duration-300 ease-in-out ${isOpen ? 'w-64' : 'w-16'} bg-blue-400 rounded-r-2xl`}>
        <button className="w-full py-3 px-4 flex items-center justify-between hover:bg-blue-300 transition-colors duration-200" onClick={toggleSidebar}>
          <div className="flex items-center">
            <Menu className="w-6 h-6 mr-3 text-white" /> {isOpen && <span className="text-lg font-medium text-white">Menu</span>}
          </div>
          {isOpen && <ChevronLeft className="w-5 h-5 text-white" />}
        </button>

        <Link to="/soal" className="w-full py-3 px-4 flex items-center hover:bg-blue-300 transition-colors duration-200">
          <FileText className="w-6 h-6 mr-3 text-white" />
          {isOpen && <span className="text-white">Soal</span>}
        </Link>

        <div className="mt-auto">
          <Link to="/registration" className="w-full py-3 px-4 flex items-center hover:bg-blue-300 transition-colors duration-200">
            <LogOut className="w-6 h-6 mr-3 text-white" />
            {isOpen && <span className="text-white">Logout</span>}
          </Link>
        </div>
      </nav>

      <div className="flex-1 p-6">
        <button onClick={() => setModalOpen(true)} className="mb-4 px-4 py-2 bg-blue-500 text-white rounded">Tambah Ujian</button>

        <div className="overflow-x-auto">
          <table className="min-w-full bg-white border border-gray-200 rounded-lg overflow-hidden">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">ID Ujian</th>
                <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Lembaga</th>
                <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Jumlah Peserta</th>
                <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Tanggal</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {examData.map((exam) => (
                <tr key={exam.id}>
                  <td className="px-4 py-2 whitespace-nowrap">
                    <label className="inline-flex items-center mb-5 cursor-pointer">
                      <input type="checkbox" checked={exam.status} onChange={() => handleStatusChange(exam.id)} className="sr-only peer"/>
                      <div className={`relative w-11 h-6 ${exam.status ? 'bg-blue-600' : 'bg-gray-200'} peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-blue-300 rounded-full`}></div>
                    </label>
                  </td>
                  <td className="px-4 py-2 whitespace-nowrap text-sm font-medium text-gray-900">{exam.id}</td>
                  <td className="px-4 py-2 whitespace-nowrap text-sm text-gray-500">{exam.lembaga}</td>
                  <td className="px-4 py-2 whitespace-nowrap text-sm text-gray-500">{exam.jumlahPeserta}</td>
                  <td className="px-4 py-2 whitespace-nowrap text-sm text-gray-500">{exam.tanggal}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Modal */}
      <Dialog open={modalOpen} onClose={() => setModalOpen(false)} className="fixed z-10 inset-0 overflow-y-auto">
        <div className="flex items-center justify-center min-h-screen">
          <Dialog.Overlay className="fixed inset-0 bg-black opacity-30" />
          <div className="bg-white rounded-lg p-6 z-20">
            <Dialog.Title className="text-lg font-medium mb-4">Tambah Ujian</Dialog.Title>
            <form onSubmit={(e) => { e.preventDefault(); handleSubmit(); }}>
              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700">Nama</label>
                <input 
                  type="text" 
                  name="nama" 
                  value={newExam.nama} 
                  onChange={handleInputChange} 
                  className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-500" 
                  required 
                />
              </div>
              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700">Tanggal</label>
                <input 
                  type="date" 
                  name="tanggal" 
                  value={newExam.tanggal} 
                  onChange={handleInputChange} 
                  className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-500" 
                  required 
                />
              </div>
              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700">Subject</label>
                <input 
                  type="text" 
                  name="subject" 
                  value={newExam.subject} 
                  onChange={handleInputChange} 
                  className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-500" 
                  required 
                />
              </div>
              <div className="flex items-center mb-4">
                <input 
                  type="checkbox" 
                  name="status" 
                  checked={newExam.status} 
                  onChange={() => setNewExam({ ...newExam, status: !newExam.status })} 
                  className="mr-2" 
                />
                <label className="text-sm font-medium text-gray-700">Status</label>
              </div>
              <button type="submit" className="w-full px-4 py-2 bg-blue-600 text-white rounded">Simpan</button>
            </form>
          </div>
        </div>
      </Dialog>
    </div>
  );
}

export default Ujian;
